import React from "react";
import { Route, Switch, useHistory } from "react-router-dom";
import { useState } from "react";
import Header from "./Header";
import Footer from "./Footer";
import Main from "./Main";
import EditProfilePopup from "./EditProfilePopup";
import AddPostPopup from "./AddPostPopup";
import UpdateAvatarPopup from "./UpdateAvatarPopup";
import ImagePopup from "./ImagePopup";
import Api from "../utils/Api";
import { CurrentUserContext } from "../contexts/CurrentUserContext";
import Login from "./Login";
import ProtectedRout from "./ProtectedRoute";
import InfoTooltip from "./InfoTooltip";
import Register from "./Register";
import * as auth from "../utils/Auth";



function App() {
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = useState(false);
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = useState(false);
  const [selectedCard, setSelectedCard] = useState(null);
  const [currentUser, setCurrentUser] = useState({});
  const [cards, setCards] = React.useState([]);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const history = useHistory();


  // React.useEffect(() => {
  //   Promise.all([Api.getUserInfoApi(), Api.getInitialCards()]).then(
  //     ([userInfo, cards]) => {
  //       setCurrentUser(userInfo);
  //       setCards(cards);
  //     }
  //   );
  // }, []);

  function getUserInfo() {
  Api.getUserInfoApi()
  .then(data => {setCurrentUser(data)})
  .catch(err => console.log(err))
}

function getInitialCards() {
  Api.getInitialCards()
  .then(cards => setCards([...cards]))
  .catch(err => console.log(err))
}

React.useEffect(() => {
  if (isLoggedIn) {
    getUserInfo();
    getInitialCards();
  }
}, [isLoggedIn])


  function handleCardClick(card) {
    setSelectedCard(card);
  }

  function onEditProfile() {
    setIsEditProfilePopupOpen(true);
  }

  function onAddPlace() {
    setIsAddPlacePopupOpen(true);
  }

  function onEditAvatar() {
    setIsEditAvatarPopupOpen(true);
  }

  function closeAllPopups() {
    setIsEditProfilePopupOpen(false);
    setIsAddPlacePopupOpen(false);
    setIsEditAvatarPopupOpen(false);
    setSelectedCard(null);
  }

  function handleCardLike(card) {
    // Снова проверяем, есть ли уже лайк на этой карточке
    const isLiked = card.likes.some((i) => i._id === currentUser._id);
    // Отправляем запрос в API и получаем обновлённые данные карточки
    Api.changeCardLikeStatus(card._id, !isLiked)
      .then((newCard) => {
        setCards((state) =>
          state.map((c) => (c._id === card._id ? newCard : c))
        );
      })
      .catch((err) => console.log(err));
  }

  function handleCardDelete(card) {
    const isOwn = card.owner._id === currentUser._id;
    Api.deletePost(card._id)
      .then(setCards((state) => state.filter((c) => c._id !== card._id)))
      .catch((err) => console.log(err));
  }

  function handleUpdateUser({ name, about }) {
    Api.updateUserInfo({ name, about })
      .then((res) => {
        setCurrentUser(res);
        closeAllPopups();
      })
      .catch((err) => console.log(err));
  }

  function handleUpdateAvatar({ avatar }) {
    Api.updateAvatar({ avatar })
      .then((res) => {
        setCurrentUser(res);
        closeAllPopups();
      })
      .catch((err) => console.log(err));
  }

  function handleAddPlaceSubmit({ name, link }) {
    Api.addNewCard({ name, link })
      .then((newCard) => {
        setCards([newCard, ...cards]);
        closeAllPopups();
      })
      .catch((err) => console.log(err));
  }

  function handleAuthorization({ password, email }) {
    auth.login({ password, email }).then((data) => {
      console.log(data);
      if (data.token) {
        setIsLoggedIn(true);
        console.log(isLoggedIn);
        localStorage.setItem("token", data.token);
        history.push("/");
      }
    });
  }

  return (
    <CurrentUserContext.Provider value={currentUser}>
      <div className="App">
        <Header />
        <Switch>
          <ProtectedRout
            exact
            path="/"
            isLoggedIn={isLoggedIn}
            children={
              <>
                <Main
                  onEditProfileClick={onEditProfile}
                  onAddPlaceClick={onAddPlace}
                  onEditAvatarClick={onEditAvatar}
                  onCardClick={handleCardClick}
                  onCardLike={handleCardLike}
                  onCardDelete={handleCardDelete}
                  cards={cards}
                />
                <Footer />
                <EditProfilePopup
                  isOpen={isEditProfilePopupOpen}
                  onClose={closeAllPopups}
                  onUpdateUser={handleUpdateUser}
                />
                <AddPostPopup
                  isOpen={isAddPlacePopupOpen}
                  onClose={closeAllPopups}
                  onAddPlace={handleAddPlaceSubmit}
                />
                <UpdateAvatarPopup
                  isOpen={isEditAvatarPopupOpen}
                  onClose={closeAllPopups}
                  onUpdateAvatar={handleUpdateAvatar}
                />
                <ImagePopup card={selectedCard} onClose={closeAllPopups} />
                <InfoTooltip />
              </>
            }
          />

          <Route path={"/sign-up"}>
            <Register />
          </Route>

          <Route path={"/login"}>
            <Login onAuthorization={handleAuthorization} />
          </Route>
        </Switch>
      </div>
    </CurrentUserContext.Provider>
  );
}

export default App;
