import React from "react";
import logoMesto from "../images/logo.svg";
import Sign from "./Sign";
import { Switch, Link, Route, useHistory } from "react-router-dom";
import MediaQuery from "react-responsive";
import burger_menu from "../images/burger-menu.svg";
import close_img from "../images/popup__icon-close.svg";

function Header({
  email,
  onUserInfoMobileOpen,
  isUserInfoMobOpen,
  onUserInfoMobileClose,
}) {
  const history = useHistory();

  function loggedOut() {
    onUserInfoMobileClose();
    localStorage.removeItem("token");
    history.push("/login");
    console.log(isUserInfoMobOpen);
  }

  return (
    <>
      <MediaQuery maxWidth={900}>
        {isUserInfoMobOpen && <Sign onLoggedOut={loggedOut} email={email} />}
      </MediaQuery>
      <header className="header">
        <img className="header__logo" src={logoMesto} alt="Логотип Mesto" />
        <MediaQuery maxWidth={900}>
          <Switch>
            <Route exact path={"/"}>
              {isUserInfoMobOpen ? (

                <button className="header__button" onClick={{onUserInfoMobileClose}} type="Button"></button>

              ) : (
                <Link onClick={onUserInfoMobileOpen}>
                  <img className="header__menu" src={burger_menu} alt="Меню" />
                </Link>
              )}
            </Route>
            <Route path="/sign-up">
              <Link className="header__link" to="/login">
                Войти
              </Link>
            </Route>
            <Route path="/login">
              <Link className="header__link" to="/sign-up">
                Регистрация
              </Link>
            </Route>
          </Switch>
        </MediaQuery>

        <MediaQuery minWidth={900}>
          <Switch>
            <Route exact path="/">
              <Sign email={email} onLogedOut={loggedOut} />
            </Route>
            <Route path="/sign-up">
              <Link className="header__link" to="/login">
                Войти
              </Link>
            </Route>
            <Route path="/login">
              <Link className="header__link" to="/sign-up">
                Регистрация
              </Link>
            </Route>
          </Switch>
        </MediaQuery>
      </header>
    </>
  );
}

export default Header;
